import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import 'bootstrap/dist/css/bootstrap.min.css';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const initialForm = {
  player: '',
  opponent: '',
  date: '',
  result: '', // ヒット/四球/三振/アウト/得点 など
  rbi: '',
  run: ''
};

function calcStats(records) {
  // 選手ごとに集計
  const stats = {};
  records.forEach((rec) => {
    if (!stats[rec.player]) {
      stats[rec.player] = {
        ab: 0, // 打数
        h: 0,  // 安打
        bb: 0, // 四球
        so: 0, // 三振
        rbi: 0, // 打点
        run: 0, // 得点
      };
    }
    // 打数・安打・四球・三振
    if (rec.result === 'ヒット') {
      stats[rec.player].ab += 1;
      stats[rec.player].h += 1;
    } else if (rec.result === '四球') {
      stats[rec.player].bb += 1;
    } else if (rec.result === '三振') {
      stats[rec.player].ab += 1;
      stats[rec.player].so += 1;
    } else if (rec.result === 'アウト') {
      stats[rec.player].ab += 1;
    }
    // 打点
    stats[rec.player].rbi += Number(rec.rbi || 0);
    // 得点
    stats[rec.player].run += Number(rec.run || 0);
  });
  // 出塁率・打率計算
  Object.values(stats).forEach((s) => {
    s.avg = s.ab > 0 ? (s.h / s.ab).toFixed(3) : '-';
    s.obp = (s.ab + s.bb) > 0 ? ((s.h + s.bb) / (s.ab + s.bb)).toFixed(3) : '-';
  });
  return stats;
}

function App() {
  const [form, setForm] = useState(initialForm);
  const [records, setRecords] = useState([]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!form.player || !form.result) return;
    setRecords([...records, form]);
    setForm(initialForm);
  };

  const stats = calcStats(records);
  const players = Object.keys(stats);

  // グラフ用データ
  const barData = {
    labels: players,
    datasets: [
      {
        label: '安打',
        data: players.map((p) => stats[p].h),
        backgroundColor: 'rgba(54, 162, 235, 0.6)'
      },
      {
        label: '打数',
        data: players.map((p) => stats[p].ab),
        backgroundColor: 'rgba(255, 99, 132, 0.4)'
      },
      {
        label: '四球',
        data: players.map((p) => stats[p].bb),
        backgroundColor: 'rgba(255, 206, 86, 0.5)'
      },
      {
        label: '三振',
        data: players.map((p) => stats[p].so),
        backgroundColor: 'rgba(153, 102, 255, 0.4)'
      },
      {
        label: '打点',
        data: players.map((p) => stats[p].rbi),
        backgroundColor: 'rgba(75, 192, 192, 0.4)'
      },
      {
        label: '得点',
        data: players.map((p) => stats[p].run),
        backgroundColor: 'rgba(255, 159, 64, 0.4)'
      }
    ]
  };

  return (
    <div className="container mt-4">
      <h2>少年野球スコア集計システム</h2>
      <form className="row g-3 mb-4" onSubmit={handleSubmit}>
        <div className="col-md-2">
          <input type="text" className="form-control" name="player" value={form.player} onChange={handleChange} placeholder="選手名" required />
        </div>
        <div className="col-md-2">
          <input type="text" className="form-control" name="opponent" value={form.opponent} onChange={handleChange} placeholder="対戦相手" />
        </div>
        <div className="col-md-2">
          <input type="date" className="form-control" name="date" value={form.date} onChange={handleChange} />
        </div>
        <div className="col-md-2">
          <select className="form-select" name="result" value={form.result} onChange={handleChange} required>
            <option value="">打席結果</option>
            <option value="ヒット">ヒット</option>
            <option value="四球">四球</option>
            <option value="三振">三振</option>
            <option value="アウト">アウト</option>
          </select>
        </div>
        <div className="col-md-1">
          <input type="number" className="form-control" name="rbi" value={form.rbi} onChange={handleChange} placeholder="打点" min="0" />
        </div>
        <div className="col-md-1">
          <input type="number" className="form-control" name="run" value={form.run} onChange={handleChange} placeholder="得点" min="0" />
        </div>
        <div className="col-md-2 d-grid">
          <button type="submit" className="btn btn-primary">追加</button>
        </div>
      </form>

      <h4>成績一覧</h4>
      <div className="table-responsive">
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>選手名</th>
              <th>打数</th>
              <th>安打</th>
              <th>四球</th>
              <th>三振</th>
              <th>打点</th>
              <th>得点</th>
              <th>打率</th>
              <th>出塁率</th>
            </tr>
          </thead>
          <tbody>
            {players.map((p) => (
              <tr key={p}>
                <td>{p}</td>
                <td>{stats[p].ab}</td>
                <td>{stats[p].h}</td>
                <td>{stats[p].bb}</td>
                <td>{stats[p].so}</td>
                <td>{stats[p].rbi}</td>
                <td>{stats[p].run}</td>
                <td>{stats[p].avg}</td>
                <td>{stats[p].obp}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <h4>選手別成績グラフ</h4>
      <div style={{ maxWidth: 900 }}>
        <Bar
          data={barData}
          options={{
            responsive: true,
            plugins: {
              legend: { position: 'top' },
              title: { display: true, text: '選手別成績' }
            }
          }}
        />
      </div>
    </div>
  );
}

export default App;
